import React, { useState } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
  IconButton,
  useTheme,
  useMediaQuery,
  Collapse,
  ListItemButton,
  Toolbar,
  ListItemIcon,
  Avatar,
} from '@mui/material';
import {
  Menu as MenuIcon,
  ExpandLess,
  ExpandMore,
  FreeBreakfast,
  LocalDrink,
  EmojiFoodBeverage,
  Coffee,
  Icecream,
  LocalBar,
  LocalCafe,
  Blender,
  Restaurant,
} from '@mui/icons-material';

const categories = [
  {
    name: 'Healthy Break Fast',
    path: '/healthy-breakfast',
    icon: <FreeBreakfast />,
    image: 'https://source.unsplash.com/100x100/?healthy-breakfast',
  },
  {
    name: 'Healthy Juices',
    path: '/healthy-juices',
    icon: <LocalDrink />,
    image: 'https://source.unsplash.com/100x100/?fresh-juice',
  },
  {
    name: 'Herbal Teas',
    path: '/herbal-teas',
    icon: <EmojiFoodBeverage />,
    image: 'https://source.unsplash.com/100x100/?herbal-tea',
  },
  {
    name: 'Hot Beverages',
    path: '/hot-beverages',
    icon: <Coffee />,
    image: 'https://source.unsplash.com/100x100/?coffee',
  },
  {
    name: 'Ice Creams',
    path: '/ice-creams',
    icon: <Icecream />,
    image: 'https://source.unsplash.com/100x100/?ice-cream',
  },
  {
    name: 'Mocktails',
    path: '/mocktails',
    icon: <LocalBar />,
    image: 'https://source.unsplash.com/100x100/?mocktail',
  },
  {
    name: 'Refreshing Drinks',
    path: '/refreshing-drinks',
    icon: <LocalCafe />,
    image: 'https://source.unsplash.com/100x100/?cold-drink',
  },
  {
    name: 'Shakes',
    path: '/shakes',
    icon: <Blender />,
    image: 'https://source.unsplash.com/100x100/?milkshake',
  },
  {
    name: 'Snacks',
    path: '/snacks',
    icon: <Restaurant />,
    image: 'https://source.unsplash.com/100x100/?healthy-snacks',
  },
];

const drawerWidth = 280;

function Sidebar() {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const [mobileOpen, setMobileOpen] = useState(false);
  const [productsOpen, setProductsOpen] = useState(true);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const handleProductsClick = () => {
    setProductsOpen(!productsOpen);
  };

  const drawer = (
    <Box>
      <Toolbar />
      <Box sx={{ p: 2 }}>
        <ListItemButton
          onClick={handleProductsClick}
          sx={{
            bgcolor: 'primary.main',
            color: 'white',
            borderRadius: 1,
            mb: 2,
            '&:hover': {
              bgcolor: 'primary.dark',
            },
          }}
        >
          <ListItemText 
            primary="Our Products"
            primaryTypographyProps={{
              variant: 'h6',
              component: 'div',
            }}
          />
          {productsOpen ? <ExpandLess /> : <ExpandMore />}
        </ListItemButton>

        <Collapse in={productsOpen} timeout="auto" unmountOnExit>
          <List>
            {categories.map((category) => (
              <ListItem
                key={category.path}
                component={RouterLink}
                to={category.path}
                sx={{
                  borderBottom: '1px solid',
                  borderColor: 'divider',
                  '&:hover': {
                    bgcolor: 'primary.light',
                    color: 'white',
                    '& .MuiListItemIcon-root': {
                      color: 'white',
                    },
                  },
                  textDecoration: 'none',
                  color: 'inherit',
                  pl: 2,
                  py: 1.5,
                }}
              >
                <ListItemIcon sx={{ minWidth: 40, color: 'primary.main' }}>
                  {category.icon}
                </ListItemIcon>
                <ListItemText 
                  primary={category.name}
                  sx={{ mr: 2 }}
                />
                <Avatar
                  src={category.image}
                  alt={category.name}
                  variant="rounded"
                  sx={{ 
                    width: 40, 
                    height: 40,
                    border: '1px solid',
                    borderColor: 'divider',
                  }}
                />
              </ListItem>
            ))}
          </List>
        </Collapse>
      </Box>
    </Box>
  );

  return (
    <>
      {isMobile && (
        <IconButton
          color="inherit"
          aria-label="open drawer"
          edge="start"
          onClick={handleDrawerToggle}
          sx={{ mr: 2, display: { sm: 'none' } }}
        >
          <MenuIcon />
        </IconButton>
      )}
      <Box
        component="nav"
        sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
      >
        {isMobile ? (
          <Drawer
            variant="temporary"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            ModalProps={{
              keepMounted: true,
            }}
            sx={{
              display: { xs: 'block', sm: 'none' },
              '& .MuiDrawer-paper': {
                boxSizing: 'border-box',
                width: drawerWidth,
              },
            }}
          >
            {drawer}
          </Drawer>
        ) : (
          <Drawer
            variant="permanent"
            sx={{
              display: { xs: 'none', sm: 'block' },
              '& .MuiDrawer-paper': {
                boxSizing: 'border-box',
                width: drawerWidth,
                borderRight: '1px solid',
                borderColor: 'divider',
              },
            }}
            open
          >
            {drawer}
          </Drawer>
        )}
      </Box>
    </>
  );
}

export default Sidebar;
