import React, { useState } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
  Typography,
  IconButton,
  useTheme,
  useMediaQuery,
} from '@mui/material';
import { Menu as MenuIcon } from '@mui/icons-material';

const categories = [
  { name: 'Healthy Break Fast', path: '/healthy-breakfast' },
  { name: 'Healthy Juices', path: '/healthy-juices' },
  { name: 'Herbal Teas', path: '/herbal-teas' },
  { name: 'Hot Beverages', path: '/hot-beverages' },
  { name: 'Ice Creams', path: '/ice-creams' },
  { name: 'Mocktails', path: '/mocktails' },
  { name: 'Refreshing Drinks', path: '/refreshing-drinks' },
  { name: 'Shakes', path: '/shakes' },
  { name: 'Snacks', path: '/snacks' },
];

const drawerWidth = 240;

function Sidebar() {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const [mobileOpen, setMobileOpen] = useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <Box sx={{ p: 2 }}>
      <Typography
        variant="h6"
        component="div"
        sx={{
          bgcolor: 'primary.main',
          color: 'white',
          p: 2,
          mb: 2,
          borderRadius: 1,
        }}
      >
        Our Products
      </Typography>
      <List>
        {categories.map((category) => (
          <ListItem
            key={category.path}
            component={RouterLink}
            to={category.path}
            sx={{
              borderBottom: '1px solid',
              borderColor: 'divider',
              '&:hover': {
                bgcolor: 'primary.light',
                color: 'white',
              },
              textDecoration: 'none',
              color: 'inherit',
            }}
          >
            <ListItemText primary={category.name} />
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <>
      {isMobile && (
        <IconButton
          color="inherit"
          aria-label="open drawer"
          edge="start"
          onClick={handleDrawerToggle}
          sx={{ mr: 2, display: { sm: 'none' } }}
        >
          <MenuIcon />
        </IconButton>
      )}
      <Box
        component="nav"
        sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
      >
        {isMobile ? (
          <Drawer
            variant="temporary"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
            sx={{
              display: { xs: 'block', sm: 'none' },
              '& .MuiDrawer-paper': {
                boxSizing: 'border-box',
                width: drawerWidth,
              },
            }}
          >
            {drawer}
          </Drawer>
        ) : (
          <Drawer
            variant="permanent"
            sx={{
              display: { xs: 'none', sm: 'block' },
              '& .MuiDrawer-paper': {
                boxSizing: 'border-box',
                width: drawerWidth,
                borderRight: '1px solid',
                borderColor: 'divider',
              },
            }}
            open
          >
            {drawer}
          </Drawer>
        )}
      </Box>
    </>
  );
}

export default Sidebar;
