import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Button,
  Container,
  Grid,
  Typography,
  Card,
  CardMedia,
  CardContent,
  CardActions,
} from '@mui/material';

const featuredJuices = [
  {
    id: 1,
    name: 'Immunity Booster',
    description: 'Orange, Carrot, and Ginger blend',
    image: 'https://source.unsplash.com/400x300/?orange-juice',
    price: '$5.99',
  },
  {
    id: 2,
    name: 'Green Detox',
    description: 'Spinach, Apple, and Mint refresher',
    image: 'https://source.unsplash.com/400x300/?green-juice',
    price: '$6.99',
  },
  {
    id: 3,
    name: 'Berry Blast',
    description: 'Mixed berries with pomegranate',
    image: 'https://source.unsplash.com/400x300/?berry-juice',
    price: '$7.99',
  },
];

function Home() {
  const navigate = useNavigate();

  return (
    <Box>
      {/* Hero Section */}
      <Box
        sx={{
          bgcolor: 'primary.main',
          color: 'white',
          py: 8,
          textAlign: 'center',
        }}
      >
        <Container>
          <Typography variant="h2" component="h1" gutterBottom>
            Fresh & Healthy Juices
          </Typography>
          <Typography variant="h5" component="h2" gutterBottom>
            Discover the taste of real fruits
          </Typography>
          <Button
            variant="contained"
            color="secondary"
            size="large"
            onClick={() => navigate('/menu')}
            sx={{ mt: 4 }}
          >
            View Our Menu
          </Button>
        </Container>
      </Box>

      {/* Featured Juices */}
      <Container sx={{ py: 8 }}>
        <Typography variant="h4" component="h2" gutterBottom textAlign="center">
          Featured Juices
        </Typography>
        <Grid container spacing={4} sx={{ mt: 2 }}>
          {featuredJuices.map((juice) => (
            <Grid item xs={12} sm={6} md={4} key={juice.id}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <CardMedia
                  component="img"
                  height="200"
                  image={juice.image}
                  alt={juice.name}
                />
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography gutterBottom variant="h5" component="h3">
                    {juice.name}
                  </Typography>
                  <Typography>{juice.description}</Typography>
                  <Typography variant="h6" color="primary" sx={{ mt: 2 }}>
                    {juice.price}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" color="primary">
                    Add to Cart
                  </Button>
                  <Button size="small" color="primary">
                    Learn More
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
}

export default Home;
